import "time"

func connectWithRetry(addr string, maxRetries int) (net.Conn, error) {
    var conn net.Conn
    var err error
    for i := 0; i < maxRetries; i++ {
        conn, err = net.DialTimeout("tcp", addr, 5*time.Second)
        if err == nil {
            return conn, nil
        }
        time.Sleep(time.Duration(1<<uint(i)) * time.Second)
    }
    return nil, err
}
