'use client';
import { useEffect, useState } from 'react';

export default function DynamicData() {
  const [data, setData] = useState(null);
  useEffect(() => {
    fetch('/api/data').then(r => r.json()).then(setData);
  }, []);
  return data ? <div>{data.value}</div> : <p>Loading...</p>;
}
